#!/usr/bin/env pythonfrom __future__ import print_function, division#This is actually Python 3 code but I'm trying to make it backward compatibleimport timestart = time.clock()M, N = 100, 999result = 0for i in range(M, N+1):    for j in range(i, N+1):        prod = i*j        if prod > result:            forward = str(prod)            reverse = forward[-1::-1]            if forward == reverse:                result = prodend = time.clock()-start            print('Answer:', result, 'Time:', end*1000, 'ms')